sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if ( newValue === '') {
		return;
	}

	var whatNeededOptions = document.getElementById('x_58872_needit_needit.u_what_needed').options;
	for( var i=0; i<whatNeededOptions.length; i++) {
		var optionValue = whatNeededOptions[i].value;
		if( !optionValue.includes( newValue ) ) {
			console.log('removeOption ' + newValue );
			g_form.removeOption('u_what_needed', optionValue );
		}
	}	
}
